import React, { useEffect, useState } from "react";
import DM2PromptEditor from "./components/DM2PromptEditor";
import CurrentPromptPanel from "./components/CurrentPromptPanel";
import ImageDropZone from "./components/ImageDropZone";
import { analyzeImage, textToPrompt, refinePrompt } from "./services/gemini";
import { generateWithImagesREST } from "./helpers/gemini";
import BackgroundCanvas from "./components/BackgroundCanvas";
import BrandHeader from "./components/BrandHeader";
import { composePrompt } from "./lib/utils";

// Helper function to convert files to data URLs
const getImageDataUrls = async (files: File[]): Promise<string[]> => {
  const promises = files.map(file => {
    return new Promise<string>((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result as string);
      reader.onerror = error => reject(error);
    });
  });
  return Promise.all(promises);
};

// Local type to coordinate speed across components
type SpeedMode = 'Fast' | 'Quality';

function App() {
  const [prompt, setPrompt] = useState("");
  const [editorSeed, setEditorSeed] = useState<string>("");
  const [isGenerating, setIsGenerating] = useState(false);
  const [images, setImages] = useState<File[]>([]);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [autoAnalyze, setAutoAnalyze] = useState(true);
  const [editorExpanded, setEditorExpanded] = useState(false);
  const [lastSource, setLastSource] = useState<"edge" | "gemini-mm" | "gemini-text" | "subject" | "scene" | "style" | undefined>(undefined);
  
  // Debug logging for state changes
  useEffect(() => {
    console.log("ðŸ”„ Prompt state changed:", prompt ? `"${prompt.substring(0, 50)}..."` : "EMPTY");
  }, [prompt]);

  useEffect(() => {
    console.log("ðŸ”„ LastSource state changed:", lastSource);
  }, [lastSource]);
  const [speedMode, setSpeedMode] = useState<SpeedMode>('Fast');
  
  // State for independent image analysis
  const [subjectImages, setSubjectImages] = useState<File[]>([]);
  const [sceneImages, setSceneImages] = useState<File[]>([]);
  const [styleImages, setStyleImages] = useState<File[]>([]);
  const [isAnalyzingSubject, setIsAnalyzingSubject] = useState(false);
  const [isAnalyzingScene, setIsAnalyzingScene] = useState(false);
  const [isAnalyzingStyle, setIsAnalyzingStyle] = useState(false);
  const [autoAnalyzeSubject, setAutoAnalyzeSubject] = useState(true);
  const [autoAnalyzeScene, setAutoAnalyzeScene] = useState(true);
  const [autoAnalyzeStyle, setAutoAnalyzeStyle] = useState(true);
  
  // Store individual analysis results
  const [subjectAnalysis, setSubjectAnalysis] = useState<string>('');
  const [sceneAnalysis, setSceneAnalysis] = useState<string>('');
  const [styleAnalysis, setStyleAnalysis] = useState<string>('');

  // Debug function to clear subject analysis
  const clearSubjectAnalysis = () => {
    console.log("ðŸ§¹ Clearing subject analysis state");
    setSubjectAnalysis('');
  };
  
  // Toggle states for using style/scene descriptors
  const [useStyle, setUseStyle] = useState<boolean>(true);
  const [useScene, setUseScene] = useState<boolean>(true);
  const [isSimpleStyleActive, setIsSimpleStyleActive] = useState<boolean>(false);
  const [originalPromptBeforeSimple, setOriginalPromptBeforeSimple] = useState<string>('');
  
  // Preview states for image thumbnails
  const [subjectPreview, setSubjectPreview] = useState<string | undefined>(undefined);
  const [scenePreview, setScenePreview] = useState<string | undefined>(undefined);
  const [stylePreview, setStylePreview] = useState<string | undefined>(undefined);



  const handleImageFiles = async (files: File[]) => {
    if (!files) return;
    setImages(files);
  };

  // New: role handlers wired to ImageDropZone
  const handleStyleFile = (file?: File) => setStyleFile(file);
  const handleSceneFile = (file?: File) => setSceneFile(file);

  const handleSend = async (finalPrompt: string) => {
    console.log("ðŸš€ðŸš€ðŸš€ HANDLE SEND TRIGGERED! ðŸš€ðŸš€ðŸš€");
    console.log("ðŸš€ handleSend called with prompt:", finalPrompt.substring(0, 100) + (finalPrompt.length > 100 ? "..." : ""));
    console.log("ðŸš€ Current images count:", images.length);
    console.log("ðŸš€ Analysis states:", { subjectAnalysis, styleAnalysis, sceneAnalysis, useStyle, useScene });
    setIsGenerating(true);
    
    try {
      // Compose prompt with optional style/scene descriptors and subject analysis
      const composed = composePrompt({
        userText: finalPrompt,
        contentSummary: subjectAnalysis,
        style: styleAnalysis,
        scene: sceneAnalysis,
        useStyle,
        useScene,
      });
      console.log("ðŸš€ Composed prompt:", composed);
      console.log("ðŸš€ Composed prompt length:", composed.length);

      if (images.length > 0) {
        console.log("ðŸš€ Taking IMAGE path - analyzing with Gemini Vision");
        // For now, just use the first image for analysis and generate a prompt
        const result = await analyzeImage(images[0], { 
          detail: speedMode === 'Quality' ? 'detailed' : 'medium',
          tags: true 
        });
        
        console.log("ðŸ” Image analysis result:", result);
        
        const promptInput = `${composed}. Based on this image: ${result.description}`;
        console.log("ðŸ“ About to call textToPrompt with:", promptInput);
        
        // Generate an enhanced prompt based on the image analysis
        const enhancedPrompt = await textToPrompt(
          promptInput,
          undefined,
          speedMode === 'Quality' ? 'high detail, professional quality' : 'good quality'
        );
        
        console.log("âœ… Enhanced prompt generated:", enhancedPrompt);
        setPrompt(enhancedPrompt);
        setEditorSeed(enhancedPrompt);
        setLastSource("gemini-mm");
      } else {
        console.log("ðŸš€ Taking TEXT-ONLY path - generating with Gemini");
        const result = await textToPrompt(composed);
        console.log("ðŸš€ Text generation result:", result.substring(0, 100) + (result.length > 100 ? "..." : ""));
        setPrompt(result);
        setEditorSeed(result);
        setLastSource("gemini-text");
      }
    } catch (err2) {
      console.error("Gemini call failed:", err2);
      console.error("Error details:", {
        message: err2.message,
        stack: err2.stack,
        name: err2.name
      });
      
      // More specific error messages
      let errorMessage = "An error occurred while generating. Please try again.";
      if (err2.message?.includes('API key')) {
        errorMessage = "API key error: Please check your VITE_GOOGLE_API_KEY environment variable.";
      } else if (err2.message?.includes('quota') || err2.message?.includes('429')) {
        errorMessage = "API quota exceeded: You've hit your Gemini API limit. Please wait and try again later.";
      } else if (err2.message?.includes('401') || err2.message?.includes('403')) {
        errorMessage = "Authentication error: Please check your API key permissions.";
      }
      
      alert(errorMessage);
    } finally {
      setIsGenerating(false);
    }
  };

  const handleReanalyze = async () => {
    console.log("ðŸ”„ Reanalyze triggered");
    
    // Reanalyze main images if they exist
    if (images.length > 0) {
      setIsAnalyzing(true);
      try {
        // Convert image to data URL for Gemini API
        const imageDataUrls = await getImageDataUrls(images);
        if (imageDataUrls.length === 0) {
          throw new Error("Failed to process image");
        }

        // Get API key and model
        const apiKey = import.meta.env.VITE_GEMINI_API_KEY;
        if (!apiKey) {
          throw new Error("Gemini API key not found");
        }

        const model = speedMode === 'Quality' ? 'gemini-2.5-pro' : 'gemini-2.5-flash';

        // Define generation config based on speed mode
        const generationConfig = speedMode === 'Quality'
          ? { temperature: 0.4, topP: 0.8, topK: 40, maxOutputTokens: 2048 }
          : { temperature: 0.7, topP: 0.9, topK: 40, maxOutputTokens: 1024 };

        // Create comprehensive analysis prompt
        const analysisPrompt = speedMode === 'Quality'
          ? "Analyze this image comprehensively and describe what you see. Include details about the subject, setting, composition, colors, lighting, style, and any notable elements. Provide a detailed description suitable for creating image generation prompts."
          : "Describe this image in detail, including the subject, setting, colors, and style.";

        // Call Gemini API
        const response = await generateWithImagesREST({
          apiKey,
          model,
          text: analysisPrompt,
          imageDataUrls,
          generationConfig
        });

        // Generate a prompt based on the analysis
        const generatedPrompt = await textToPrompt(
          `Create an image generation prompt based on this analysis: ${response}`,
          undefined,
          speedMode === 'Quality' ? 'high detail, professional quality' : 'good quality'
        );
        
        setPrompt(generatedPrompt);
        setEditorSeed(generatedPrompt);
        setLastSource("gemini-mm");
      } catch (error: any) {
        console.error("Reanalyze main images failed:", error);
        
        // Handle specific API errors
        if (error.message?.includes('429') || error.message?.includes('rate limit')) {
          console.error("Rate limit exceeded. Please try again later.");
        } else if (error.message?.includes('401') || error.message?.includes('authentication')) {
          console.error("Authentication failed. Please check your API key.");
        } else if (error.message?.includes('403') || error.message?.includes('forbidden')) {
          console.error("Access forbidden. Please check your API permissions.");
        }
      } finally {
        setIsAnalyzing(false);
      }
    }

    // Reanalyze subject images if they exist
    if (subjectImages.length > 0) {
      setIsAnalyzingSubject(true);
      try {
        // Convert image to data URL for Gemini API
        const imageDataUrls = await getImageDataUrls(subjectImages);
        if (imageDataUrls.length === 0) {
          throw new Error("Failed to process image");
        }

        // Get API key and model
        const apiKey = import.meta.env.VITE_GEMINI_API_KEY;
        if (!apiKey) {
          throw new Error("Gemini API key not found");
        }

        const model = speedMode === 'Quality' ? 'gemini-2.5-pro' : 'gemini-2.5-flash';

        // Define generation config based on speed mode
        const generationConfig = speedMode === 'Quality'
          ? { temperature: 0.4, topP: 0.8, topK: 40, maxOutputTokens: 2048 }
          : { temperature: 0.7, topP: 0.9, topK: 40, maxOutputTokens: 1024 };

        // Create focused subject analysis prompt
        const subjectPrompt = speedMode === 'Quality'
          ? "Analyze and describe the main subject(s) in this image. Focus on: people, animals, objects, their appearance, clothing, poses, expressions, and physical characteristics. Provide a detailed description suitable for image generation prompts. Do NOT describe the background, setting, or artistic style."
          : "Describe the main subject or person in this image. Focus on their appearance, clothing, and pose, not the background or style.";

        // Call Gemini API
        const response = await generateWithImagesREST({
          apiKey,
          model,
          text: subjectPrompt,
          imageDataUrls,
          generationConfig
        });

        // Clean up the response
        let cleanedResponse = response
          .replace(/^(Subject:|Person:|Character:|Main subject:)\s*/i, '') // Remove subject prefixes
          .replace(/\s+/g, ' ') // Normalize whitespace
          .trim();
        
        console.log("ðŸ‘¤ Subject reanalysis result:", cleanedResponse);
        setSubjectAnalysis(cleanedResponse);
      } catch (error: any) {
        console.error("Reanalyze subject failed:", error);
        
        // Handle specific API errors
        if (error.message?.includes('429') || error.message?.includes('rate limit')) {
          console.error("Rate limit exceeded. Please try again later.");
        } else if (error.message?.includes('401') || error.message?.includes('authentication')) {
          console.error("Authentication failed. Please check your API key.");
        } else if (error.message?.includes('403') || error.message?.includes('forbidden')) {
          console.error("Access forbidden. Please check your API permissions.");
        }
      } finally {
        setIsAnalyzingSubject(false);
      }
    }

    // Reanalyze scene images if they exist
    if (sceneImages.length > 0) {
      setIsAnalyzingScene(true);
      try {
        // Convert image to data URL for Gemini API
        const imageDataUrls = await getImageDataUrls(sceneImages);
        if (imageDataUrls.length === 0) {
          throw new Error("Failed to process image");
        }

        // Get API key and model
        const apiKey = import.meta.env.VITE_GEMINI_API_KEY;
        if (!apiKey) {
          throw new Error("Gemini API key not found");
        }

        const model = speedMode === 'Quality' ? 'gemini-2.5-pro' : 'gemini-2.5-flash';

        // Define generation config based on speed mode
        const generationConfig = speedMode === 'Quality'
          ? { temperature: 0.4, topP: 0.8, topK: 40, maxOutputTokens: 2048 }
          : { temperature: 0.7, topP: 0.9, topK: 40, maxOutputTokens: 1024 };

        // Create focused scene analysis prompt
        const scenePrompt = speedMode === 'Quality'
          ? "Analyze and describe the setting, environment, and background of this image. Focus on: location/setting, architecture, landscape, weather/time of day, environmental details, spatial composition, and contextual elements. Provide a detailed description suitable for image generation prompts. Do NOT describe the people, objects, or artistic style."
          : "Describe the setting, location, and environment in this image. Focus on the background, architecture, and setting, not the people or style.";

        // Call Gemini API
        const response = await generateWithImagesREST({
          apiKey,
          model,
          text: scenePrompt,
          imageDataUrls,
          generationConfig
        });

        // Clean up the response
        let cleanedResponse = response
          .replace(/^(Scene:|Setting:|Environment:|Background:)\s*/i, '') // Remove scene prefixes
          .replace(/\s+/g, ' ') // Normalize whitespace
          .trim();
        
        console.log("ðŸ” Scene reanalysis result:", cleanedResponse);
        setSceneAnalysis(cleanedResponse);
      } catch (error: any) {
        console.error("Reanalyze scene failed:", error);
        
        // Handle specific API errors
        if (error.message?.includes('429') || error.message?.includes('rate limit')) {
          console.error("Rate limit exceeded. Please try again later.");
        } else if (error.message?.includes('401') || error.message?.includes('authentication')) {
          console.error("Authentication failed. Please check your API key.");
        } else if (error.message?.includes('403') || error.message?.includes('forbidden')) {
          console.error("Access forbidden. Please check your API permissions.");
        }
      } finally {
        setIsAnalyzingScene(false);
      }
    }

    // Reanalyze style images if they exist
    if (styleImages.length > 0) {
      setIsAnalyzingStyle(true);
      try {
        // Convert image to data URL for Gemini API
        const imageDataUrls = await getImageDataUrls(styleImages);
        if (imageDataUrls.length === 0) {
          throw new Error("Failed to process image");
        }

        // Get API key and model
        const apiKey = import.meta.env.VITE_GEMINI_API_KEY;
        if (!apiKey) {
          throw new Error("Gemini API key not found");
        }

        const model = speedMode === 'Quality' ? 'gemini-2.5-pro' : 'gemini-2.5-flash';

        // Define generation config based on speed mode
        const generationConfig = speedMode === 'Quality'
          ? { temperature: 0.4, topP: 0.8, topK: 40, maxOutputTokens: 2048 }
          : { temperature: 0.7, topP: 0.9, topK: 40, maxOutputTokens: 1024 };

        // Create focused style analysis prompt
        const stylePrompt = speedMode === 'Quality'
          ? "Analyze the artistic style, visual treatment, composition, and aesthetic approach of this image. Focus on: lighting style, color palette, mood/atmosphere, artistic technique, visual composition, and overall aesthetic treatment. Provide a concise but detailed description suitable for image generation prompts. Do NOT describe the subject or people in the image."
          : "Describe the visual style, lighting, colors, and artistic approach of this image. Focus on style elements, not the subject.";

        // Call Gemini API
        const response = await generateWithImagesREST({
          apiKey,
          model,
          text: stylePrompt,
          imageDataUrls,
          generationConfig
        });

        // Clean up the response
        let cleanedResponse = response
          .replace(/^(Style:|Visual Style:|Artistic Style:)\s*/i, '') // Remove style prefixes
          .replace(/\s+/g, ' ') // Normalize whitespace
          .trim();
        
        console.log("ðŸŽ¨ Style reanalysis result:", cleanedResponse);
        setStyleAnalysis(cleanedResponse);
      } catch (error: any) {
        console.error("Reanalyze style failed:", error);
        
        // Handle specific API errors
        if (error.message?.includes('429') || error.message?.includes('rate limit')) {
          console.error("Rate limit exceeded. Please try again later.");
        } else if (error.message?.includes('401') || error.message?.includes('authentication')) {
          console.error("Authentication failed. Please check your API key.");
        } else if (error.message?.includes('403') || error.message?.includes('forbidden')) {
          console.error("Access forbidden. Please check your API permissions.");
        }
      } finally {
        setIsAnalyzingStyle(false);
      }
    }
  };

  // Auto-analyze images when they are added, honoring Speed Mode (existing behavior)
  useEffect(() => {
    let cancelled = false;
    const run = async () => {
      if (!autoAnalyze || images.length === 0) return;
      setIsAnalyzing(true);
      try {
        // Convert image to data URL for Gemini API
        const imageDataUrls = await getImageDataUrls(images);
        if (imageDataUrls.length === 0) {
          throw new Error("Failed to process image");
        }

        // Get API key and model
        const apiKey = import.meta.env.VITE_GEMINI_API_KEY;
        if (!apiKey) {
          throw new Error("Gemini API key not found");
        }

        const model = speedMode === 'Quality' ? 'gemini-2.5-pro' : 'gemini-2.5-flash';

        // Define generation config based on speed mode
        const generationConfig = speedMode === 'Quality'
          ? { temperature: 0.4, topP: 0.8, topK: 40, maxOutputTokens: 2048 }
          : { temperature: 0.7, topP: 0.9, topK: 40, maxOutputTokens: 1024 };

        // Create comprehensive analysis prompt
        const analysisPrompt = speedMode === 'Quality'
          ? "Analyze this image comprehensively and describe what you see. Include details about the subject, setting, composition, colors, lighting, style, and any notable elements. Provide a detailed description suitable for creating image generation prompts."
          : "Describe this image in detail, including the subject, setting, colors, and style.";

        // Call Gemini API
        const response = await generateWithImagesREST({
          apiKey,
          model,
          text: analysisPrompt,
          imageDataUrls,
          generationConfig
        });
        
        // Generate a prompt based on the analysis
        const generatedPrompt = await textToPrompt(
          `Create an image generation prompt based on this analysis: ${response}`,
          undefined,
          speedMode === 'Quality' ? 'high detail, professional quality' : 'good quality'
        );
        
        if (!cancelled) {
          setPrompt(generatedPrompt);
          setEditorSeed(generatedPrompt);
          setLastSource("gemini-mm");
        }
      } catch (error: any) {
        console.error("Auto-analyze call failed:", error);
        
        // Handle specific API errors
        if (error.message?.includes('429') || error.message?.includes('rate limit')) {
          console.error("Rate limit exceeded. Please try again later.");
        } else if (error.message?.includes('401') || error.message?.includes('authentication')) {
          console.error("Authentication failed. Please check your API key.");
        } else if (error.message?.includes('403') || error.message?.includes('forbidden')) {
          console.error("Access forbidden. Please check your API permissions.");
        }
      } finally {
        if (!cancelled) setIsAnalyzing(false);
      }
    };
    run();
    return () => {
      cancelled = true;
    };
  }, [images, autoAnalyze, speedMode]);

  // Auto-analyze subject images when they are added
  useEffect(() => {
    let cancelled = false;
    const run = async () => {
      if (!autoAnalyzeSubject || subjectImages.length === 0) return;
      
      // Clear previous analysis to prevent cached results
      setSubjectAnalysis('');
      setIsAnalyzingSubject(true);
      
      try {
        // Convert image to data URL for Gemini API
        const imageDataUrls = await getImageDataUrls(subjectImages);
        if (imageDataUrls.length === 0) {
          throw new Error("Failed to process image");
        }

        // Get API key and model
        const apiKey = import.meta.env.VITE_GEMINI_API_KEY;
        if (!apiKey) {
          throw new Error("Gemini API key not found");
        }

        const model = speedMode === 'Quality' ? 'gemini-2.5-pro' : 'gemini-2.5-flash';

        // Define generation config based on speed mode
        const generationConfig = speedMode === 'Quality'
          ? { temperature: 0.4, topP: 0.8, topK: 40, maxOutputTokens: 2048 }
          : { temperature: 0.7, topP: 0.9, topK: 40, maxOutputTokens: 1024 };

        // Create focused subject analysis prompt
        const subjectPrompt = speedMode === 'Quality'
          ? "Analyze and describe the main subjects, people, objects, and characters in this image. Focus on: physical appearance, clothing/attire, poses/actions, expressions, key objects, and notable details. Provide a detailed description suitable for image generation prompts. Do NOT describe the background, setting, or artistic style."
          : "Describe the main subjects, people, and objects in this image. Focus on appearance, clothing, and actions, not the background or style.";

        // Call Gemini API
        const response = await generateWithImagesREST({
          apiKey,
          model,
          text: subjectPrompt,
          imageDataUrls,
          generationConfig
        });

        // Clean up the response
        let cleanedResponse = response
          .replace(/^(Subject:|Main Subject:|People:|Objects:)\s*/i, '') // Remove subject prefixes
          .replace(/\s+/g, ' ') // Normalize whitespace
          .trim();

        console.log("ðŸ” Auto-Analyze Subject - Response:", cleanedResponse);
        if (!cancelled) {
          setSubjectAnalysis(cleanedResponse);
        }
      } catch (error: any) {
        console.error("Auto-analyze subject failed:", error);
        
        // Handle specific API errors
        if (error.message?.includes('429') || error.message?.includes('rate limit')) {
          console.error("Rate limit exceeded. Please try again later.");
        } else if (error.message?.includes('401') || error.message?.includes('authentication')) {
          console.error("Authentication failed. Please check your API key.");
        } else if (error.message?.includes('403') || error.message?.includes('forbidden')) {
          console.error("Access forbidden. Please check your API permissions.");
        }
      } finally {
        if (!cancelled) setIsAnalyzingSubject(false);
      }
    };
    run();
    return () => {
      cancelled = true;
    };
  }, [subjectImages, autoAnalyzeSubject, speedMode]);

  // Clear subject analysis when subject images are removed
  useEffect(() => {
    if (subjectImages.length === 0 && subjectAnalysis) {
      setSubjectAnalysis('');
    }
  }, [subjectImages, subjectAnalysis]);

  // Auto-analyze scene images when they are added
  useEffect(() => {
    let cancelled = false;
    const run = async () => {
      if (!autoAnalyzeScene || sceneImages.length === 0) return;
      setIsAnalyzingScene(true);
      
      try {
        // Convert image to data URL for Gemini API
        const imageDataUrls = await getImageDataUrls(sceneImages);
        if (imageDataUrls.length === 0) {
          throw new Error("Failed to process image");
        }

        // Get API key and model
        const apiKey = import.meta.env.VITE_GEMINI_API_KEY;
        if (!apiKey) {
          throw new Error("Gemini API key not found");
        }

        const model = speedMode === 'Quality' ? 'gemini-2.5-pro' : 'gemini-2.5-flash';

        // Define generation config based on speed mode
        const generationConfig = speedMode === 'Quality'
          ? { temperature: 0.4, topP: 0.8, topK: 40, maxOutputTokens: 2048 }
          : { temperature: 0.7, topP: 0.9, topK: 40, maxOutputTokens: 1024 };

        // Create focused scene analysis prompt
        const scenePrompt = speedMode === 'Quality'
          ? "Analyze and describe the setting, environment, and background of this image. Focus on: location/setting, architecture, landscape, weather/time of day, environmental details, spatial composition, and contextual elements. Provide a detailed description suitable for image generation prompts. Do NOT describe the people, objects, or artistic style."
          : "Describe the setting, location, and environment in this image. Focus on the background, architecture, and setting, not the people or style.";

        // Call Gemini API
        const response = await generateWithImagesREST({
          apiKey,
          model,
          text: scenePrompt,
          imageDataUrls,
          generationConfig
        });

        // Clean up the response
        let cleanedResponse = response
          .replace(/^(Scene:|Setting:|Environment:|Background:)\s*/i, '') // Remove scene prefixes
          .replace(/\s+/g, ' ') // Normalize whitespace
          .trim();

        console.log("ðŸ” Auto-Analyze Scene - Response:", cleanedResponse);
        if (!cancelled) {
          setSceneAnalysis(cleanedResponse);
        }
      } catch (error: any) {
        console.error("Auto-analyze scene failed:", error);
        
        // Handle specific API errors
        if (error.message?.includes('429') || error.message?.includes('rate limit')) {
          console.error("Rate limit exceeded. Please try again later.");
        } else if (error.message?.includes('401') || error.message?.includes('authentication')) {
          console.error("Authentication failed. Please check your API key.");
        } else if (error.message?.includes('403') || error.message?.includes('forbidden')) {
          console.error("Access forbidden. Please check your API permissions.");
        }
      } finally {
        if (!cancelled) setIsAnalyzingScene(false);
      }
    };
    run();
    return () => {
      cancelled = true;
    };
  }, [sceneImages, autoAnalyzeScene, speedMode]);

  // Clear scene analysis when scene images are removed
  useEffect(() => {
    if (sceneImages.length === 0 && sceneAnalysis) {
      setSceneAnalysis('');
    }
  }, [sceneImages, sceneAnalysis]);

  // Auto-analyze style images when they are added
  useEffect(() => {
    let cancelled = false;
    const run = async () => {
      if (!autoAnalyzeStyle || styleImages.length === 0) return;
      setIsAnalyzingStyle(true);
      
      try {
        // Convert image to data URL for Gemini API
        const imageDataUrls = await getImageDataUrls(styleImages);
        if (imageDataUrls.length === 0) {
          throw new Error("Failed to process image");
        }

        // Get API key and model
        const apiKey = import.meta.env.VITE_GEMINI_API_KEY;
        if (!apiKey) {
          throw new Error("Gemini API key not found");
        }

        const model = speedMode === 'Quality' ? 'gemini-2.5-pro' : 'gemini-2.5-flash';

        // Define generation config based on speed mode
        const generationConfig = speedMode === 'Quality'
          ? { temperature: 0.4, topP: 0.8, topK: 40, maxOutputTokens: 2048 }
