          .trim();

        console.log("ðŸŽ¨ Auto-Analyze Style - Response:", cleanedResponse);
        if (!cancelled) {
          setStyleAnalysis(cleanedResponse);
        }
      } catch (error: any) {
        console.error("Auto-analyze style failed:", error);
        
