export default App;

import React, { useMemo, useState, useEffect, useRef } from 'react';
import { cn } from "../lib/utils";

export type RewriteStyle = 'Descriptive' | 'Concise' | 'Marketing' | 'Technical';
export type SpeedMode = 'Fast' | 'Quality';

interface DM2PromptEditorProps {
  onSend: (finalPrompt: string) => void;
  onClear?: () => void;
  initialText?: string;
  onResizeStart?: () => void;
  onResizeEnd?: (height: number) => void;
  // Added: allow App to control/read speed mode
  initialSpeedMode?: SpeedMode;
  onSpeedModeChange?: (mode: SpeedMode) => void;
  // Style/Scene descriptors and toggles
  styleDesc?: string;
  sceneDesc?: string;
  useStyle?: boolean;
  useScene?: boolean;
  onToggleStyle?: (v: boolean) => void;
  onToggleScene?: (v: boolean) => void;
}

export default function DM2PromptEditor({ onSend, onClear, initialText, onResizeStart, onResizeEnd, initialSpeedMode, onSpeedModeChange, styleDesc = "", sceneDesc = "", useStyle = true, useScene = true, onToggleStyle, onToggleScene }: DM2PromptEditorProps) {
  const [rewriteStyle, setRewriteStyle] = useState<RewriteStyle>('Descriptive');
  const [speedMode, setSpeedMode] = useState<SpeedMode>('Fast');
  const [autoRefine, setAutoRefine] = useState<boolean>(true);
  const [promptCount, setPromptCount] = useState<number>(1);
  const [draft, setDraft] = useState<boolean>(false);
  const [text, setText] = useState<string>('');
  const [editorHeight, setEditorHeight] = useState<number>(200);

  useEffect(() => {
    if (typeof initialText !== 'undefined') {
      setText(initialText);
    }
  }, [initialText]);

  // Sync incoming speed mode from parent if provided
  useEffect(() => {
    if (typeof initialSpeedMode !== 'undefined') {
      setSpeedMode(initialSpeedMode);
    }
  }, [initialSpeedMode]);

  const textareaRef = useRef<HTMLTextAreaElement>(null);
  const dragState = useRef({
    pressed: false,
    dragging: false,
    startX: 0,
    startY: 0,
    scrollLeft: 0,
    scrollTop: 0,
  });
  const resizeState = useRef({
    pressed: false,
    dragging: false,
    startY: 0,
    startHeight: 0,
  });
  const minHeight = 0;
  const maxHeight = 600;

  useEffect(() => {
    // Pointer events are used directly on the textarea; no window listeners needed.
  }, []);

  const onTextAreaPointerDown = (e: React.PointerEvent<HTMLTextAreaElement>) => {
    if (e.button !== 0) return;
    const el = textareaRef.current;
    if (!el) return;
    dragState.current.pressed = true;
    dragState.current.dragging = false;
    dragState.current.startX = e.clientX;
    dragState.current.startY = e.clientY;
    dragState.current.scrollTop = el.scrollTop;
    dragState.current.scrollLeft = el.scrollLeft;
    // Use pointer capture to reliably receive move/up events
    (el as any).setPointerCapture?.(e.pointerId);
  };

  const onTextAreaPointerMove = (e: React.PointerEvent<HTMLTextAreaElement>) => {
    const el = textareaRef.current;
    if (!el || !dragState.current.pressed) return;
    const dx = e.clientX - dragState.current.startX;
    const dy = e.clientY - dragState.current.startY;

    if (!dragState.current.dragging) {
      if (Math.abs(dx) > 4 || Math.abs(dy) > 4) {
        dragState.current.dragging = true;
        el.style.userSelect = 'none';
        el.style.cursor = 'grabbing';
      } else {
        return;
      }
    }

    // prevent text selection while dragging
    e.preventDefault();
    el.scrollTop = dragState.current.scrollTop - dy;
    el.scrollLeft = dragState.current.scrollLeft - dx;
  };

  const onTextAreaPointerUp = (e: React.PointerEvent<HTMLTextAreaElement>) => {
    const el = textareaRef.current;
    dragState.current.pressed = false;
    dragState.current.dragging = false;
    if (el) {
      el.style.userSelect = 'text';
      el.style.cursor = 'grab';
      (el as any).releasePointerCapture?.(e.pointerId);
    }
  };

  const onGripPointerDown = (e: React.PointerEvent<HTMLDivElement>) => {
    const el = e.currentTarget as HTMLDivElement;
    resizeState.current.pressed = true;
    resizeState.current.dragging = false;
    resizeState.current.startY = e.clientY;
    resizeState.current.startHeight = editorHeight;
    (el as any).setPointerCapture?.(e.pointerId);
    document.body.style.userSelect = 'none';
    onResizeStart?.();
  };
  const onGripPointerMove = (e: React.PointerEvent<HTMLDivElement>) => {
    if (!resizeState.current.pressed) return;
    const dy = e.clientY - resizeState.current.startY;
    if (!resizeState.current.dragging) {
      if (Math.abs(dy) <= 4) return;
      resizeState.current.dragging = true;
    }
    e.preventDefault();
    const next = Math.max(minHeight, Math.min(maxHeight, resizeState.current.startHeight + dy));
    setEditorHeight(next);
  };
  const onGripPointerUp = (e: React.PointerEvent<HTMLDivElement>) => {
    const el = e.currentTarget as HTMLDivElement;
    resizeState.current.pressed = false;
    resizeState.current.dragging = false;
    (el as any).releasePointerCapture?.(e.pointerId);
    document.body.style.userSelect = 'auto';
    onResizeEnd?.(editorHeight);
  };

  const isSendDisabled = useMemo(() => text.trim().length === 0, [text]);

  const handleSend = () => {
    // Stub rewrite: build final prompt matching controls
    const header = `[${rewriteStyle} | ${speedMode}${autoRefine ? ' +AutoRefine' : ''} | count:${promptCount}${draft ? ' | draft' : ''}]`;
    const finalPrompt = `${header}\n\n${text.trim()}`;
    onSend(finalPrompt);
  };

  const handleClear = () => {
    // Clear only the editor controls and text
    setText('');
    setDraft(false);
    setPromptCount(1);
    setAutoRefine(true);
    setSpeedMode('Fast');
    setRewriteStyle('Descriptive');
    setEditorHeight(200);
    onSpeedModeChange?.('Fast');
  };

  const handleClearAll = () => {
    // Delegate full app-wide clearing to parent
    onClear?.();
  };

  return (
    <div className="bg-panel dark:bg-dark-panel rounded-xl border-2 border-border dark:border-dark-border p-4 flex flex-col h-full">
      <div className="flex items-center justify-between">
        <h3 className="text-sm font-semibold text-text-primary dark:text-dark-text-primary">Prompt Editor</h3>
      </div>

      <div className="mt-2 flex-1 overflow-y-auto no-scrollbar space-y-3">

      {/* Rewrite Style */}
      <div className="flex items-center justify-between">
        <label className="text-xs text-text-secondary dark:text-dark-text-secondary">Rewrite Style</label>
        <select
          value={rewriteStyle}
          onChange={(e) => setRewriteStyle(e.target.value as RewriteStyle)}
          className="rounded-md bg-panel-secondary dark:bg-dark-panel-secondary text-text-primary dark:text-dark-text-primary border border-border dark:border-dark-border text-sm px-2 py-1"
        >
          <option>Descriptive</option>
          <option>Concise</option>
          <option>Marketing</option>
          <option>Technical</option>
        </select>
      </div>

      {/* Speed Mode */}
      <div className="flex items-center justify-between">
        <label className="text-xs text-text-secondary dark:text-dark-text-secondary">Speed Mode</label>
        <div className="flex gap-2">
          {(['Fast', 'Quality'] as SpeedMode[]).map((m) => (
            <button
              key={m}
              onClick={() => { setSpeedMode(m); onSpeedModeChange?.(m); }}
              className={`px-3 py-1 rounded-full text-xs border-2 border-border dark:border-dark-border transition ${
                speedMode === m ? 'bg-brand-accent text-white' : 'bg-panel-secondary dark:bg-dark-panel-secondary text-text-primary dark:text-dark-text-primary'
              }`}
            >
              {m}
            </button>
          ))}
        </div>
      </div>

      {/* Auto Refine */}
      <div className="flex items-center justify-between">
        <label className="text-xs text-text-secondary dark:text-dark-text-secondary">Auto Refine (Quality)</label>
        <div className="flex gap-2">
          {['Off', 'On'].map((label) => {
            const active = (label === 'On' ? autoRefine : !autoRefine);
            return (
              <button
                key={label}
                onClick={() => setAutoRefine(label === 'On')}
                className={`px-3 py-1 rounded-full text-xs border-2 border-border dark:border-dark-border transition ${
                  active ? 'bg-brand-accent text-white' : 'bg-panel-secondary dark:bg-dark-panel-secondary text-text-primary dark:text-dark-text-primary'
                }`}
              >
                {label}
              </button>
            );
          })}
        </div>
      </div>

      {/* Prompt Count */}
      <div className="flex items-center justify-between">
        <label className="text-xs text-text-secondary dark:text-dark-text-secondary">Prompt Count</label>
        <div className="flex items-center gap-2">
          <button
            onClick={() => setPromptCount((c) => Math.max(1, c - 1))}
            className="w-7 h-7 rounded-full bg-panel-secondary dark:bg-dark-panel-secondary border-2 border-border dark:border-dark-border text-text-primary dark:text-dark-text-primary"
          >
            âˆ’
          </button>
          <span className="text-sm font-medium text-text-primary dark:text-dark-text-primary">{promptCount}</span>
          <button
            onClick={() => setPromptCount((c) => Math.min(99, c + 1))}
            className="w-7 h-7 rounded-full bg-panel-secondary dark:bg-dark-panel-secondary border-2 border-border dark:border-dark-border text-text-primary dark:text-dark-text-primary"
          >
            +
          </button>
        </div>
      </div>

      {/* Draft toggle */}
      <div className="flex items-center justify-between">
        <label className="text-xs text-text-secondary dark:text-dark-text-secondary">Draft</label>
        <div className="flex gap-2">
          {['Off', 'On'].map((label) => {
            const active = (label === 'On' ? draft : !draft);
            return (
              <button
                key={label}
                onClick={() => setDraft(label === 'On')}
                className={`px-3 py-1 rounded-full text-xs border-2 border-border dark:border-dark-border transition ${
                  active ? 'bg-brand-accent text-white' : 'bg-panel-secondary dark:bg-dark-panel-secondary text-text-primary dark:text-dark-text-primary'
                }`}
              >
                {label}
              </button>
            );
          })}
        </div>
      </div>

      {/* Textarea */}
      <div className="-mt-1">
        <label className="text-xs text-text-secondary dark:text-dark-text-secondary">Enter your prompt here...</label>
        <textarea
          ref={textareaRef}
          value={text}
          onChange={(e) => setText(e.target.value)}
          onPointerDown={onTextAreaPointerDown}
          onPointerMove={onTextAreaPointerMove}
          onPointerUp={onTextAreaPointerUp}
          className="w-full rounded-lg bg-dark-background/70 text-dark-text-primary placeholder:text-dark-text-secondary border-2 border-dark-border mt-1 pt-0 px-2 pb-2 text-[12px] resize-none overflow-y-auto no-scrollbar cursor-text"
          style={{ height: editorHeight }}
          placeholder="Enter your prompt here..."
        />
        <div
          aria-label="Drag to open/resize editor"
          onPointerDown={onGripPointerDown}
          onPointerMove={onGripPointerMove}
          onPointerUp={onGripPointerUp}
          className="mt-1 h-4 w-full rounded-md bg-panel-secondary dark:bg-dark-panel-secondary border-2 border-border dark:border-dark-border flex items-center justify-center cursor-ns-resize select-none"
          style={{ touchAction: 'none' }}
        >
          <div className="w-10 h-[2px] rounded bg-border dark:bg-dark-border" />
        </div>

      </div>

      </div>

      {/* Footer buttons */}
      <div className="mt-auto pt-2 flex items-center justify-between">
        <div className="flex items-center gap-2">
          <button
            onClick={handleClear}
            className="px-2 py-1 text-sm rounded-lg bg-background dark:bg-dark-background border-2 border-border dark:border-dark-border text-text-primary dark:text-dark-text-primary"
          >
            Clear
          </button>
          <button
            onClick={handleClearAll}
            className="px-2 py-1 text-sm rounded-lg bg-background dark:bg-dark-background border-2 border-border dark:border-dark-border text-text-primary dark:text-dark-text-primary"
            title="Clear all panels: editor, images, and prompt"
          >
            Clear All
          </button>
        </div>
        <button
          disabled={isSendDisabled}
          onClick={handleSend}
          className="px-3 py-1 text-sm rounded-lg bg-brand-accent hover:bg-brand-accent/90 text-white font-medium disabled:opacity-60"
        >
          Send
        </button>
      </div>
      {/* Style/Scene chips */}
      {(styleDesc || sceneDesc) && (
        <div className="flex flex-wrap items-center gap-2">
          {styleDesc && (
            <button
              type="button"
              onClick={() => onToggleStyle?.(!useStyle)}
              className={`px-3 py-1 rounded-full text-xs border-2 border-border dark:border-dark-border transition ${useStyle ? 'bg-brand-accent text-white' : 'bg-panel-secondary dark:bg-dark-panel-secondary text-text-primary dark:text-dark-text-primary'}`}
              title="Toggle style descriptor"
            >
              {useStyle ? `Style: ${styleDesc}` : 'Style (off)'}
            </button>
          )}
          {sceneDesc && (
            <button
              type="button"
              onClick={() => onToggleScene?.(!useScene)}
              className={`px-3 py-1 rounded-full text-xs border-2 border-border dark:border-dark-border transition ${useScene ? 'bg-brand-accent text-white' : 'bg-panel-secondary dark:bg-dark-panel-secondary text-text-primary dark:text-dark-text-primary'}`}
              title="Toggle scene descriptor"
            >
              {useScene ? `Scene: ${sceneDesc}` : 'Scene (off)'}
            </button>
          )}
        </div>
      )}
    </div>
  );
}

import React, { useRef, useState } from 'react';

interface ImageDropZoneProps {
  images: File[];
  onImagesChange: (images: File[]) => void;
  onRunAnalysis: () => void;
  isAnalyzing: boolean;
  onClearGeneral?: () => void;
  autoAnalyze: boolean;
  onAutoAnalyzeChange: (enabled: boolean) => void;
  subjectImages: File[];
  onSubjectImagesChange: (images: File[]) => void;
  onRunSubjectAnalysis: () => void;
  isAnalyzingSubject: boolean;
  subjectPreview?: string;
  autoAnalyzeSubject: boolean;
  onAutoAnalyzeSubjectChange: (enabled: boolean) => void;
  sceneImages: File[];
  onSceneImagesChange: (images: File[]) => void;
  onRunSceneAnalysis: () => void;
  isAnalyzingScene: boolean;
  scenePreview?: string;
  autoAnalyzeScene: boolean;
  onAutoAnalyzeSceneChange: (enabled: boolean) => void;
  styleImages: File[];
  onStyleImagesChange: (images: File[]) => void;
  onRunStyleAnalysis: () => void;
  isAnalyzingStyle: boolean;
  stylePreview?: string;
  autoAnalyzeStyle: boolean;
  onAutoAnalyzeStyleChange: (enabled: boolean) => void;
  onAnalyzeAllStyles: () => void;
}

export default function ImageDropZone({
  images,
  onImagesChange,
  onRunAnalysis,
  isAnalyzing,
  onClearGeneral,
  autoAnalyze,
  onAutoAnalyzeChange,
  subjectImages,
  onSubjectImagesChange,
  onRunSubjectAnalysis,
  isAnalyzingSubject,
  subjectPreview,
  autoAnalyzeSubject,
  onAutoAnalyzeSubjectChange,
  sceneImages,
  onSceneImagesChange,
  onRunSceneAnalysis,
  isAnalyzingScene,
  scenePreview,
  autoAnalyzeScene,
  onAutoAnalyzeSceneChange,
  styleImages,
  onStyleImagesChange,
  onRunStyleAnalysis,
  isAnalyzingStyle,
  stylePreview,
  autoAnalyzeStyle,
  onAutoAnalyzeStyleChange,
  onAnalyzeAllStyles,
}: ImageDropZoneProps) {
  const fileInputRef = useRef<HTMLInputElement>(null);
  const subjectInputRef = useRef<HTMLInputElement>(null);
  const sceneInputRef = useRef<HTMLInputElement>(null);
  const styleInputRef = useRef<HTMLInputElement>(null);
  
  const [isDragOver, setIsDragOver] = useState(false);
  const [isSubjectDragOver, setIsSubjectDragOver] = useState(false);
  const [isSceneDragOver, setIsSceneDragOver] = useState(false);
  const [isStyleDragOver, setIsStyleDragOver] = useState(false);
  const [previewImage, setPreviewImage] = useState<string | null>(null);

  const handleFiles = (files: FileList) => {
    const imageFiles = Array.from(files).filter(file => file.type.startsWith('image/'));
    if (imageFiles.length > 0) {
      onImagesChange?.(imageFiles);
      
      // Create preview for the first image
      const reader = new FileReader();
      reader.onload = (e) => {
        setPreviewImage(e.target?.result as string);
      };
      reader.readAsDataURL(imageFiles[0]);
    }
  };

  const handleSubjectFiles = (files: FileList) => {
    const imageFiles = Array.from(files).filter(file => file.type.startsWith('image/'));
    if (imageFiles.length > 0) {
      onSubjectImagesChange?.(imageFiles);
    }
  };

  const handleSceneFiles = (files: FileList) => {
    const imageFiles = Array.from(files).filter(file => file.type.startsWith('image/'));
    if (imageFiles.length > 0) {
      onSceneImagesChange?.(imageFiles);
    }
  };

  const handleStyleFiles = (files: FileList) => {
    const imageFiles = Array.from(files).filter(file => file.type.startsWith('image/'));
    if (imageFiles.length > 0) {
      onStyleImagesChange?.(imageFiles);
    }
  };

  // General Analysis handlers
  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(false);
    handleFiles(e.dataTransfer.files);
  };

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(true);
  };

  const handleDragLeave = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(false);
  };

  // Subject Analysis handlers
  const handleSubjectDrop = (e: React.DragEvent) => {
    e.preventDefault();
    setIsSubjectDragOver(false);
    handleSubjectFiles(e.dataTransfer.files);
  };

  const handleSubjectDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    setIsSubjectDragOver(true);
  };

  const handleSubjectDragLeave = (e: React.DragEvent) => {
    e.preventDefault();
    setIsSubjectDragOver(false);
  };

  // Scene Analysis handlers
  const handleSceneDrop = (e: React.DragEvent) => {
    e.preventDefault();
    setIsSceneDragOver(false);
    handleSceneFiles(e.dataTransfer.files);
  };

  const handleSceneDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    setIsSceneDragOver(true);
  };

  const handleSceneDragLeave = (e: React.DragEvent) => {
    e.preventDefault();
    setIsSceneDragOver(false);
  };

  // Style Analysis handlers
  const handleStyleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    setIsStyleDragOver(false);
    handleStyleFiles(e.dataTransfer.files);
  };

  const handleStyleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    setIsStyleDragOver(true);
  };

  const handleStyleDragLeave = (e: React.DragEvent) => {
    e.preventDefault();
    setIsStyleDragOver(false);
  };

  const handleClearGeneralClick = () => {
    onClearGeneral?.();
    setPreviewImage(null);
  };

  return (
    <div className="bg-panel dark:bg-dark-panel rounded-xl border-2 border-border dark:border-dark-border h-full flex flex-col p-2">
      {/* Three Cards Container */}
      <div className="grid grid-cols-1 gap-2 flex-1">
          
          {/* Subject Analysis Card */}
          <div className="group relative">
            {/* Neon Glow Effect */}
            <div className="absolute -inset-0.5 bg-gradient-to-r from-purple-500 to-purple-600 rounded-xl blur opacity-30 group-hover:opacity-50 transition duration-300"></div>
            
            {/* Card Content */}
            <div className="relative bg-panel-secondary dark:bg-dark-panel-secondary border border-purple-500/30 rounded-xl p-2.5 h-44 flex flex-col">
              {/* Header */}
              <div className="flex items-center gap-2 mb-2">
                <div className="p-1.5 bg-purple-500/20 rounded-lg">
                  <svg className="w-4 h-4 text-purple-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                  </svg>
                </div>
                <h3 className="text-base font-semibold text-text-primary dark:text-dark-text-primary">Subject Analysis</h3>
                <button
                  onClick={onAnalyzeAllStyles}
                  disabled={!subjectImages.length && !sceneImages.length && !styleImages.length}
                  className={`ml-auto px-2 py-1 text-xs font-medium rounded-full transition-all duration-300 flex items-center gap-1 ${
                    (!subjectImages.length && !sceneImages.length && !styleImages.length)
                      ? 'bg-gray-700 text-gray-500 cursor-not-allowed'
                      : 'bg-gradient-to-r from-purple-600 to-orange-600 hover:from-purple-500 hover:to-orange-500 text-white shadow-lg hover:shadow-purple-500/25'
                  }`}
                  title="Analyze all image styles at once"
                >
                  <svg className="w-3 h-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 10V3L4 14h7v7l9-11h-7z" />
                  </svg>
                  All Styles
                </button>
              </div>

              {/* Drop Zone */}
              <div
                className={`relative border-2 border-dashed rounded-lg p-2.5 text-center transition-all duration-300 cursor-pointer flex-1 flex flex-col justify-center overflow-hidden ${
                  isSubjectDragOver
                    ? 'border-purple-400 bg-purple-500/10'
                    : 'border-purple-500/40 hover:border-purple-400 hover:bg-purple-500/5'
                }`}
                onDrop={handleSubjectDrop}
                onDragOver={handleSubjectDragOver}
                onDragLeave={handleSubjectDragLeave}
                onClick={() => subjectInputRef.current?.click()}
              >
                {isAnalyzingSubject && (
                  <div className="absolute inset-0 bg-gray-900/90 flex items-center justify-center rounded-lg">
                    <div className="flex flex-col items-center space-y-2">
                      <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-purple-500"></div>
                      <span className="text-xs text-purple-300">Analyzingâ€¦</span>
                    </div>
                  </div>
                )}
                
                {!isAnalyzingSubject && (
                  subjectPreview ? (
                    <div className="flex items-center justify-between p-2 space-x-3">
                      <p className="text-xs text-purple-300 flex-shrink-0">
                        {subjectImages.length} image{subjectImages.length !== 1 ? 's' : ''} selected
                      </p>
                      <div className="relative flex justify-center flex-1">
                        <img 
                          src={subjectPreview} 
                          alt="Subject Preview" 
                          className="max-w-full max-h-12 object-contain rounded"
                        />
                      </div>
                    </div>
                  ) : (
                    <div className="flex flex-col items-center space-y-1">
                      <svg className="w-4 h-4 text-purple-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 16a4 4 0 01-.88-7.903A5 5 0 1115.9 6L16 6a5 5 0 011 9.9M15 13l-3-3m0 0l-3 3m3-3v12" />
                      </svg>
                      <p className="text-xs text-purple-300 font-medium">Drop subject images here</p>
                    </div>
                  )
                )}
                
                <input
                  ref={subjectInputRef}
                  type="file"
                  multiple
                  accept="image/*"
                  className="hidden"
                  onChange={(e) => e.target.files && handleSubjectFiles(e.target.files)}
                />
              </div>

              {/* Helper Text */}
              <p className="text-xs text-gray-400 text-center mt-1 mb-2">Focus on people, objects, characters</p>

              {/* Action Row */}
              <div className="flex items-center justify-between gap-2">
                <label className="flex items-center space-x-1 text-xs text-gray-300">
                  <input
                    type="checkbox"
                    checked={autoAnalyzeSubject}
                    onChange={(e) => onAutoAnalyzeSubjectChange(e.target.checked)}
                    className="w-3 h-3 text-purple-600 bg-gray-700 border-gray-600 rounded focus:ring-purple-500 focus:ring-1"
                  />
                  <span>Auto-analyze</span>
                </label>
                
                <button
                  onClick={onRunSubjectAnalysis}
                  disabled={subjectImages.length === 0 || isAnalyzingSubject}
                  className={`px-2 py-1 text-xs font-medium rounded-full transition-all duration-300 ${
                    isAnalyzingSubject || subjectImages.length === 0
                      ? 'bg-gray-700 text-gray-500 cursor-not-allowed'
                      : 'bg-purple-600 hover:bg-purple-500 text-white shadow-lg hover:shadow-purple-500/25'
                  }`}
                >
                  {isAnalyzingSubject ? 'Analyzing...' : 'Analyze Subject'}
                </button>
              </div>
            </div>
          </div>

          {/* Scene Analysis Card */}
          <div className="group relative">
            {/* Neon Glow Effect */}
            <div className="absolute -inset-0.5 bg-gradient-to-r from-teal-500 to-green-600 rounded-xl blur opacity-30 group-hover:opacity-50 transition duration-300"></div>
            
            {/* Card Content */}
            <div className="relative bg-panel-secondary dark:bg-dark-panel-secondary border border-teal-500/30 rounded-xl p-2.5 h-44 flex flex-col">
              {/* Header */}
              <div className="flex items-center gap-2 mb-2">
                <div className="p-1.5 bg-teal-500/20 rounded-lg">
                  <svg className="w-4 h-4 text-teal-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l1.586-1.586a2 2 0 012.828 0L20 14m-6-6h.01M6 20h12a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2v12a2 2 0 002 2z" />
                  </svg>
                </div>
                <h3 className="text-base font-semibold text-text-primary dark:text-dark-text-primary">Scene Analysis</h3>
              </div>

              {/* Drop Zone */}
              <div
                className={`relative border-2 border-dashed rounded-lg p-2.5 text-center transition-all duration-300 cursor-pointer flex-1 flex flex-col justify-center overflow-hidden ${
                  isSceneDragOver
                    ? 'border-teal-400 bg-teal-500/10'
                    : 'border-teal-500/40 hover:border-teal-400 hover:bg-teal-500/5'
                }`}
                onDrop={handleSceneDrop}
                onDragOver={handleSceneDragOver}
                onDragLeave={handleSceneDragLeave}
                onClick={() => sceneInputRef.current?.click()}
              >
                {isAnalyzingScene && (
                  <div className="absolute inset-0 bg-gray-900/90 flex items-center justify-center rounded-lg">
                    <div className="flex flex-col items-center space-y-2">
                      <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-teal-500"></div>
                      <span className="text-xs text-teal-300">Analyzingâ€¦</span>
                    </div>
                  </div>
                )}
                
                {!isAnalyzingScene && (
                  scenePreview ? (
                    <div className="flex items-center justify-between p-2 space-x-3">
                      <p className="text-xs text-teal-300 flex-shrink-0">
                        {sceneImages.length} image{sceneImages.length !== 1 ? 's' : ''} selected
                      </p>
                      <div className="relative flex justify-center flex-1">
                        <img 
                          src={scenePreview} 
                          alt="Scene Preview" 
                          className="max-w-full max-h-12 object-contain rounded"
                        />
                      </div>
                    </div>
                  ) : (
                    <div className="flex flex-col items-center space-y-1">
                      <svg className="w-4 h-4 text-teal-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 16a4 4 0 01-.88-7.903A5 5 0 1115.9 6L16 6a5 5 0 011 9.9M15 13l-3-3m0 0l-3 3m3-3v12" />
                      </svg>
                      <p className="text-xs text-teal-300 font-medium">Drop scene images here</p>
                    </div>
                  )
                )}
                
                <input
                  ref={sceneInputRef}
                  type="file"
                  multiple
                  accept="image/*"
                  className="hidden"
                  onChange={(e) => e.target.files && handleSceneFiles(e.target.files)}
                />
              </div>

              {/* Helper Text */}
              <p className="text-xs text-gray-400 text-center mt-1 mb-2">Focus on environments, backgrounds, settings</p>

              {/* Action Row */}
              <div className="flex items-center justify-between gap-2">
                <label className="flex items-center space-x-1 text-xs text-gray-300">
