/**
 * Analyze an image using Gemini Vision
 */
export async function analyzeImage(
  imageFile: File,
  options: AnalyzeImageOptions = {}
): Promise<AnalyzeImageResult> {
  console.log('ðŸ” analyzeImage called with:', { fileName: imageFile.name, size: imageFile.size, options });

  if (!API_KEY) {
    console.error('âŒ No API key found!');
    throw new Error('Gemini API key not configured');
  }

  try {
    // Convert file to base64
    console.log('ðŸ“„ Converting file to base64...');
    const base64 = await fileToBase64(imageFile);
    console.log('âœ… Base64 conversion complete, length:', base64.length);

    // Create advanced prompt based on options
    const prompt = createAdvancedPrompt(options);

    // Prepare the image data
    const imagePart = {
      inlineData: {
        data: base64,
        mimeType: imageFile.type,
      },
    };

    // Generate content
    console.log('ðŸš€ Calling Gemini API with prompt:', prompt);
    console.log('ðŸ“¸ Image data prepared, mime type:', imageFile.type);
    const result = await model.generateContent([prompt, imagePart]);
    console.log('ðŸ“¡ API call completed, getting response...');
    const response = await result.response;
    const text = response.text();
    console.log('âœ… Response received, length:', text.length);

    // Parse advanced response components
    const parsedResult = parseAdvancedResponse(text, options);

    return parsedResult;
  } catch (error: any) {
    console.error('Error analyzing image:', error);

    // Enhanced error handling with specific error types
    if (error.message?.includes('429')) {
      throw new Error('API Rate Limit: You\'ve exceeded your Gemini API quota. Please wait a few minutes and try again.');
    } else if (error.message?.includes('401')) {
      throw new Error('API Authentication Error: Please check your VITE_GEMINI_API_KEY in the .env file.');
    } else if (error.message?.includes('403')) {
      throw new Error('API Access Forbidden: Your API key may not have access to the Gemini model.');
    } else if (error.message?.includes('400')) {
      throw new Error('Invalid Request: The image format may not be supported or the request is malformed.');
    } else if (error.message?.includes('500')) {
      throw new Error('Server Error: Gemini API is experiencing issues. Please try again later.');
    } else {
      throw new Error(`Image analysis failed: ${error.message || 'Unknown error occurred'}`);
    }
  }
}

/**
 * Generate a prompt from text description
 */
export async function textToPrompt(
  intent: string,
  style?: string,
  constraints?: string
): Promise<string> {
  console.log('ðŸ“ textToPrompt called with:', { intent, style, constraints });

  try {
    let prompt = `Convert this description into a detailed, well-structured prompt for image generation: "${intent}"`;

    if (style) {
      prompt += ` Style: ${style}.`;
    }

    if (constraints) {
      prompt += ` Constraints: ${constraints}.`;
    }

    prompt += ' Make the prompt clear, specific, and optimized for AI image generation.';

    console.log('ðŸš€ Calling server API for prompt generation with:', prompt);

    // Use server endpoint instead of direct Google API call
    const response = await fetch('http://localhost:3001/api/gemini/text', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        prompt: prompt,
        model: 'gemini-2.5-flash'
      }),
    });

    if (!response.ok) {
      const errorText = await response.text();
      console.error('Server API error:', response.status, errorText);
      throw new Error(`Server API error ${response.status}: ${errorText}`);
    }

    const result = await response.json();
    console.log('ðŸ“¡ Prompt generation API call completed');

    const generatedPrompt = result.candidates?.[0]?.content?.parts?.[0]?.text || result.text || '';
    console.log('âœ… Generated prompt received, length:', generatedPrompt.length);
    return generatedPrompt.trim();
  } catch (error) {
    console.error('Error generating prompt:', error);
    throw new Error('Failed to generate prompt. Please try again.');
  }
}

