  /**
   * Parse the AI response into structured format
   */
  private parsePromptResponse(response: string, options: PromptGenerationOptions): PromptGenerationResult {
    console.log('ðŸ” V2 parsePromptResponse called with response length:', response.length);

    // Clean up the response text
    const cleanedResponse = response.trim();
    console.log('ðŸ“ V2 Using direct text parsing for prompt');

