    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(body)
  });

  // Enhanced error-aware fallback: if access/not-found, try 2.5â†’1.5 and flashâ†’pro
  if (!res.ok) {
    const errText = await res.text();
    console.error('Gemini (images) error:', res.status, errText);
    if (/API key not valid/i.test(errText) || /INVALID_ARGUMENT/i.test(errText)) {
      if (allowFallback) {
        console.warn('Gemini API key invalid; returning generic fallback');
        const generic = inputText + ' â€” photorealistic, soft studio lighting, shallow depth of field, 50mm lens, balanced composition, high detail.';
        return generic.trim();
      }
      throw new Error('Gemini API key is invalid or not configured.');
    }
    if (allowFallback) {
      const isAccessOrNotFound = res.status === 403 || res.status === 404 || /permission|access|not\s*found|unsupported|model/i.test(errText);
      if (isAccessOrNotFound) {
        let fallbackModel = chosenModel;
        if (fallbackModel.includes('2.5')) fallbackModel = fallbackModel.replace('2.5', '1.5');
        if (fallbackModel.includes('flash')) fallbackModel = fallbackModel.replace('flash', 'pro');
        if (fallbackModel !== chosenModel) {
          return generateWithGeminiImages(inputText, imageDataUrls, fallbackModel, false);
        }
      }
    }
    throw new Error(`Gemini (images) error ${res.status}: ${errText}`);
  }

  const data = await res.json();

  // Robust extraction: first actual text part across candidates
  let textOut = '';
  for (const cand of data?.candidates ?? []) {
    for (const part of cand?.content?.parts ?? []) {
      if ((part as any)?.text) { textOut = String((part as any).text).trim(); break; }
    }
    if (textOut) break;
  }
  if (!textOut) {
    const outParts = data?.candidates?.[0]?.content?.parts ?? [];
    textOut = outParts.map((p: any) => p?.text ?? '').join('').trim();
  }
  console.log('Gemini images response:', textOut);

  // Flash â†’ Pro fallback if empty
  if (!textOut && allowFallback && chosenModel.includes('flash')) {
    const fallbackModel = chosenModel.replace('flash', 'pro');
    return generateWithGeminiImages(inputText, imageDataUrls, fallbackModel, false);
  }

  return textOut || '(No content returned)';
}

import { PromptToTextInput, PromptToTextResponse } from '../types/api';
import { config } from '../config';
import { logger } from '../logger';
import { getPrimaryModel, getFallbackModel } from '../model-resolver';
import { callGemini } from './geminiClient';
import { getPromptRefineSystemPrompt, getPromptEvaluateSystemPrompt, getPromptSimplifySystemPrompt } from '../prompts';
import { metricsStore } from '../metrics';
import { randomUUID } from 'crypto';

export async function promptToText(
  input: PromptToTextInput,
  headers: Record<string, string>
): Promise<PromptToTextResponse> {
  const traceId = randomUUID();
  const startTime = Date.now();

  const mode = headers['x-analyze-mode'] as 'fast' | 'quality' || input.mode || 'fast';
  const model = mode === 'fast' ? getPrimaryModel() : getFallbackModel();
  const timeout = mode === 'fast' ? config.timeoutMsFast : config.timeoutMsQuality;

  let systemPrompt: string;
  let userPrompt: string;

  switch (input.task) {
    case 'refine':
      systemPrompt = getPromptRefineSystemPrompt();
      userPrompt = `Prompt to refine: ${input.prompt}`;
      if (input.target_length) {
        userPrompt += `\nTarget length: approximately ${input.target_length} characters`;
      }
      break;

    case 'evaluate':
      systemPrompt = getPromptEvaluateSystemPrompt();
      userPrompt = `Prompt to evaluate: ${input.prompt}`;
      break;

    case 'simplify':
      systemPrompt = getPromptSimplifySystemPrompt();
      userPrompt = `Prompt to simplify: ${input.prompt}`;
      if (input.target_length) {
        userPrompt += `\nTarget length: approximately ${input.target_length} characters`;
      }
      break;
  }

  const fullPrompt = `${systemPrompt}\n\n${userPrompt}`;

  try {
    const result = await callGemini({
      model,
      prompt: fullPrompt,
      timeout,
    });

    let score = 0.8; // Default score
    let suggestions: string[] = [];
    let resultText = result.text;

    // Parse evaluation response
    if (input.task === 'evaluate') {
      const scoreMatch = result.text.match(/score:?\s*(0?\.\d+|1\.0)/i);
      if (scoreMatch) {
        score = parseFloat(scoreMatch[1]);
      }

      const suggestionLines = result.text
        .split('\n')
        .filter(line => line.trim().startsWith('-') || line.trim().startsWith('â€¢'))
        .map(line => line.replace(/^[-â€¢]\s*/, '').trim());

      suggestions = suggestionLines.slice(0, 5);
    }

    const latency = Date.now() - startTime;

    metricsStore.addRequest({
      trace_id: traceId,
      endpoint: '/prompt-to-text',
      mode,
      chosen_model: model,
      used_model: model,
      fallback_used: false,
      shadow_ran: false,
      latency_ms: latency,
    });

    logger.info({ trace_id: traceId, model, latency, task: input.task }, 'Prompt-to-text complete');

    return {
      result: resultText,
      score,
      suggestions,
    };
  } catch (error: any) {
    const latency = Date.now() - startTime;

    metricsStore.addRequest({
      trace_id: traceId,
      endpoint: '/prompt-to-text',
      mode,
      chosen_model: model,
      fallback_used: false,
      shadow_ran: false,
      latency_ms: latency,
      error: error.message,
    });

    logger.error({ trace_id: traceId, error: error.message }, 'Prompt-to-text failed');
    throw error;
  }
}

import Replicate from 'replicate';

// Initialize Replicate client
const replicate = new Replicate({
  auth: import.meta.env.VITE_REPLICATE_API_TOKEN,
});

export interface AnalyzeImageOptions {
  detail?: 'short' | 'medium' | 'detailed';
  tags?: boolean;
}

export interface AnalyzeImageResult {
  description: string;
  tags?: string[];
}

/**
 * Convert file to base64 data URL
 */
async function fileToBase64(file: File): Promise<string> {
  return new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.onload = () => resolve(reader.result as string);
    reader.onerror = reject;
    reader.readAsDataURL(file);
  });
}

/**
 * Upload image to a temporary hosting service for Replicate
 * For now, we'll convert to base64 and use data URLs
 */
async function prepareImageForReplicate(file: File): Promise<string> {
  // Convert file to base64 data URL
  const dataUrl = await fileToBase64(file);
  return dataUrl;
}

/**
 * Analyze an image using Replicate's img2prompt model
 */
export async function analyzeImage(
  imageFile: File,
  options: AnalyzeImageOptions = {}
): Promise<AnalyzeImageResult> {
  console.log('ðŸ” Replicate analyzeImage called with:', { 
    fileName: imageFile.name, 
    size: imageFile.size, 
    options 
  });

  const apiToken = import.meta.env.VITE_REPLICATE_API_TOKEN;
  if (!apiToken) {
    console.error('âŒ No Replicate API token found!');
    throw new Error('Replicate API token not configured. Please set VITE_REPLICATE_API_TOKEN in your .env file');
  }

  try {
    // Prepare image for Replicate
    console.log('ðŸ“„ Converting image for Replicate...');
    const imageData = await prepareImageForReplicate(imageFile);
    console.log('âœ… Image conversion complete');

    // Run the img2prompt model
    console.log('ðŸš€ Calling Replicate img2prompt model...');
    const output = await replicate.run(
      "methexis-inc/img2prompt:50adaf2d3ad20a6f911a8a9e3ccf777b263b8596fbd2c8fc26e8888f8a0edbb5",
      {
        input: {
          image: imageData,
        },
      }
    ) as string;

    console.log('âœ… Replicate API call completed');
    console.log('ðŸ“ Generated prompt:', output);

    // Process the output based on options
    let description = output;
    let tags: string[] | undefined;

    // If tags are requested, try to extract them from the description
    if (options.tags) {
      // Simple tag extraction - look for comma-separated terms
      const tagMatches = description.match(/\b\w+(?:\s+\w+)*\b/g);
      if (tagMatches) {
        tags = tagMatches.slice(0, 10); // Limit to 10 tags
      }
    }

    // Adjust description length based on detail level
    if (options.detail === 'short') {
      // Truncate to first sentence or 100 characters
      const firstSentence = description.split('.')[0];
      description = firstSentence.length > 100 
        ? description.substring(0, 100) + '...'
        : firstSentence + '.';
    } else if (options.detail === 'detailed') {
      // Keep full description as is
      description = output;
    }

    const result: AnalyzeImageResult = {
      description,
      ...(tags && { tags })
    };

    console.log('ðŸŽ¯ Final analysis result:', result);
    return result;

  } catch (error) {
    console.error('âŒ Replicate image analysis failed:', error);

    // Provide more specific error messages
    if (error instanceof Error) {
      if (error.message.includes('authentication') || error.message.includes('401')) {
        throw new Error('Replicate API authentication failed. Please check your VITE_REPLICATE_API_TOKEN.');
      } else if (error.message.includes('quota') || error.message.includes('429')) {
        throw new Error('Replicate API quota exceeded. Please try again later.');
      } else if (error.message.includes('network') || error.message.includes('fetch')) {
        throw new Error('Network error connecting to Replicate API. Please check your internet connection.');
      }
    }

    throw new Error(`Image analysis failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
  }
}

