/**
 * Refine an existing prompt (for compatibility with existing App.tsx)
 */
export async function refinePrompt(
  existingPrompt: string,
  mode: 'refine' | 'enhance' | 'simplify' = 'refine'
): Promise<string> {
  console.log('ðŸ”§ Replicate refinePrompt called with:', { existingPrompt, mode });

  try {
    // For now, use local enhancement since we don't have a specific refine model
    let refined = existingPrompt;

    switch (mode) {
      case 'enhance':
        refined = enhancePromptLocally(existingPrompt);
        break;
      case 'simplify':
        // Simplify by removing redundant words and keeping core concepts
        refined = existingPrompt
          .replace(/,\s*(highly detailed|professional quality|sharp focus|vibrant colors|excellent composition)/gi, '')
          .replace(/\s+/g, ' ')
          .trim();
        break;
      case 'refine':
      default:
        // Add some variation and enhancement
        refined = enhancePromptLocally(existingPrompt, 'artistic', 'high quality');
        break;
    }

    console.log('âœ… Prompt refinement completed:', refined);
    return refined;

  } catch (error) {
    console.error('âŒ Prompt refinement failed:', error);
    throw new Error(`Prompt refinement failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
  }
}

/**
 * Local prompt enhancement function
 */
function enhancePromptLocally(intent: string, style?: string, constraints?: string): string {
  let enhanced = intent;

  // Add style information
  if (style) {
    enhanced += `, ${style} style`;
  }

  // Add quality enhancers
  enhanced += ', highly detailed, professional quality';

  // Add constraints
  if (constraints) {
    enhanced += `, ${constraints}`;
  }

  // Add common quality terms
  enhanced += ', sharp focus, vibrant colors, excellent composition';

  return enhanced;
}

import { supabase } from '../integrations/supabase/client';

export const EDGE_FUNCTION_NAME = 'enhance-prompt';

export type EnhancePromptRequest = {
  prompt?: string;
  inputText?: string; // optional unified text key
  images?: string[]; // base64 data URLs
  imageDataUrls?: string[]; // optional unified images key
  model?: string; // e.g., 'gemini-1.5-flash' or 'gemini-1.5-pro'
};

export type EnhancePromptResponse = {
  enhancedPrompt?: string;
  output?: string;
};

export async function generatePromptViaEdge(req: EnhancePromptRequest): Promise<string> {
  if (!supabase) {
    throw new Error('Supabase client not initialized');
  }

  console.log('Invoking edge function with:', req);

  const supabaseAnonKey = import.meta.env.VITE_SUPABASE_ANON_KEY as string | undefined;

  try {
    const invokeOptions: any = { body: req };
    if (supabaseAnonKey) {
      // Include both Authorization and apikey to satisfy gateway expectations
      invokeOptions.headers = {
        Authorization: `Bearer ${supabaseAnonKey}`,
        apikey: supabaseAnonKey,
        'Content-Type': 'application/json',
      };
    }

    const { data, error } = await supabase.functions.invoke(EDGE_FUNCTION_NAME, invokeOptions);

    console.log('Edge function data:', data);
    console.log('Edge function error:', error);

    if (error) {
      throw new Error(`Edge function error: ${JSON.stringify(error)}`);
    }

    if (!data || !(data as any).enhancedPrompt) {
      throw new Error(`No prompt returned. Data: ${JSON.stringify(data)}`);
    }

    return (data as any).enhancedPrompt as string;
  } catch (err) {
    console.error('generatePromptViaEdge failed:', err);
    throw err;
  }
}

